import { signInWithPopup } from "firebase/auth";
import { getDoc } from "firebase/firestore";
import { all, call, put, takeLatest } from "redux-saga/effects";
import { auth, createUserProfileDocument, gooogleProvider, SignInWithGoogle } from "../../firebase/firebase.utils";
import { googleSignInFailure, googleSignSuccess } from "./user.actions";
import UserActionTypes from "./user.types";


export function* signInWithGoogle() {
    try {
        const { user } = yield signInWithPopup(auth, gooogleProvider);
        const userRef = yield call(createUserProfileDocument, user);
        const userSnapshot = yield getDoc(userRef);
        yield put(googleSignSuccess({ id: userSnapshot.id, ...userSnapshot.data() }));
        console.log(userRef);
    } catch (error) {
        yield put(googleSignInFailure(error));
    }
}

export function* onGoogleSignInStart() {
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle);
}

export function* userSagas() {
    yield all([call(onGoogleSignInStart)]);
}
