import { signInWithEmailAndPassword, signInWithPopup } from "firebase/auth";
import { getDoc } from "firebase/firestore";
import { __SECRET_INTERNALS_DO_NOT_USE_OR_YOU_WILL_BE_FIRED } from "react-dom";
import { all, call, put, takeLatest } from "redux-saga/effects";
import { auth, createUserProfileDocument, gooogleProvider, SignInWithGoogle } from "../../firebase/firebase.utils";
import { emailSignInFailure, emailSignSuccess, googleSignInFailure, googleSignSuccess } from "./user.actions";
import UserActionTypes from "./user.types";


export function* signInWithGoogle() {
    try {
        const { user } = yield signInWithPopup(auth, gooogleProvider);
        const userRef = yield call(createUserProfileDocument, user);
        const userSnapshot = yield getDoc(userRef);
        yield put(googleSignSuccess({ id: userSnapshot.id, ...userSnapshot.data() }));
        // console.log(userRef);
    } catch (error) {
        yield put(googleSignInFailure(error));
    }
}

export function* signInWithEmail({ payload: { email, password } }) {
    try {
        const { user } = yield signInWithEmailAndPassword(auth, email, password);
        const userRef = yield call(createUserProfileDocument, user);
        const userSnapshot = yield getDoc(userRef);
        yield put(emailSignSuccess({ id: userSnapshot.id, ...userSnapshot.data() }));


    } catch (error) {
        yield put(emailSignInFailure(error));
    }
}

export function* onGoogleSignInStart() {
    yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle);
}

export function* onEmailSignInStart() {
    yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, signInWithEmail);
}

export function* userSagas() {
    yield all([call(onGoogleSignInStart), call(onEmailSignInStart)]);

}
