import React from "react";

import './sign-in.styles.scss';

import FormInput from "../form-input/form-input.component";

class SignIn extends React.Component{
    constructor(props) {
        super(props);

        this.state={
            email: '',
            password: ''
        }
    }

    handleSubmit=event=>{
        event.preventDefault();
        this.setState({email:'',password:''})
    }

    handleChange=event=>{
        const{value,name}=event.target;

        this.setState({[name]: value})
    }

    render() {
        return (
            <div className="sign-in">
                <h2>I alread have an account</h2>
                <span>Sign in width your email and password</span>

                <form onSubmit={this.handleSubmit}>
                    <labal>Email</labal>
                    <FormInput name='email' type='email' handleChange={this.handleChange} value={this.state.email} label='email' required/>
                    <labal>Password</labal>
                   <FormInput name='password' type='password' value={this.state.email} handleChange={this.handleChange} label='password' required/>


                    <input type="submit" value="Subit Form" />
                </form>
            </div>
        )
    }
}

export default SignIn;
