import React from "react";
import { Route, Routes } from "react-router-dom";

import CollectionPage from "../category/collection.component";
import CollectionsOverview from "../../components/collections-overview/collections-overview.componenet";

import { connect } from "react-redux";
import {fetchCollectionsStartAsync} from "../../redux/shop/shop.actions";
import { createStructuredSelector} from 'reselect';
import { selectIsCollectionFetching, selectIsCollectionsLoaded } from "../../redux/shop/shop.selector";
//import Collec from "../../components/collections-overview/collections-overview.componenet";
import WithSpinner from "../../components/with-spinner/with-spinner.component";


const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);
class ShopPage extends React.Component {

    // state = {
    //     loading: true
    // };
    // unsubsribeFromSnapshot = null;

    componentDidMount() {
        const { fetchCollectionsStartAsync } = this.props;
        fetchCollectionsStartAsync();

    //     const {updateCollections}=this.props;
    //     const collectionRef=query(collection(firestore,'collections'));

    //     const getDataFromFireabase = async(collectionRef,updateCollections) =>{
    //     const snapshot = await getDocs(collectionRef);

    //     const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
    //     updateCollections(collectionsMap);
    //     this.setState({loading: false });
    //     }
    //     getDataFromFireabase(collectionRef,updateCollections);
    }

   render (){
      // const { match } = this.props;     
      //const { loading }=this.state;   
      const {isCollectionFetching, isCollectionsLoaded} = this.props;
    return (
    <div className='shop-page'>
        <Routes>
            <Route path='/' element={<CollectionsOverviewWithSpinner isLoading={isCollectionFetching}/>}/>
            <Route path=":collectionId" element={<CollectionPageWithSpinner isLoading={!isCollectionsLoaded} animate={true}/>}/>
        </Routes>
    </div>);
   }
}

const mapStateToProps = createStructuredSelector({
    selectIsCollectionFetching: selectIsCollectionFetching,
    isCollectionsLoaded: selectIsCollectionsLoaded
});

const mapDispatchToProps = dispatch => ({
    fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync())
});

export default connect(mapStateToProps,mapDispatchToProps)(ShopPage);
