const INITIAL_STATE = {
    currentUser: null
}
const userReducer = (state, action) => {
    switch (action.type) {
        case 'SET_CURRENT_USER':
            return {
                ...state,
                currentUser : action.paylad
            }
        default:
                return state;
    }
}


export default userReducer;
