import React from 'react';

import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';

import { auth,createUserProfileDocument } from '../../firebase/firebase.utils';

import './sign-up.styles.scss';

class SignUp extends React.Component{
    constructor(){
        super();

        this.state = {
            displayName: '',
            email: '',
            password: '',
            confirmPassword: ''
        }
    }

    handleSubmit = async event => {
        event.preventDefault();

        const {diplayname,email,password,confirmPassword}=this.state;

        if (password!=confirmPassword){
            alert ("passwrods don't mmach");
            return;
        }

        try{
            const {user} = await auth.createUserWithEmailAndPassword(email,password);

            await createUserProfileDocument  (user, {diplayname});

            this.setState({
                displayName: '',
                email: '',
                password: '',
                confirmPassword: ''
            });
        } catch (error) {
            console.error(error);
        }

    };

    handleChange = event => {
        const { name , value} = event.target;
        this.setState({[name]:value});
    }

    render() {
        return(
            <div className='sign-up'>
                <h2 className='title'>I do not have a account</h2>
                <span>SIgn up with your email and password</span>
                <form className='sign-upform' on Submit={this.handleSubmit}>
                    <FormInput
                        type='text'
                        name='displayName'
                        value={diplayName}
                        onChange={this.handleChange}
                        label='Display Name'
                        required
                        />
                        <FormInput
                        type='email'
                        name='email'
                        value={email}
                        onChange={this.handleChange}
                        label='Email'
                        required
                        />
                        <FormInput
                        type='password'
                        name='password'
                        value={password}
                        onChange={this.handleChange}
                        label='Password'
                        required
                        />
                        <FormInput
                        type='password'
                        name='password'
                        value={password}
                        onChange={this.handleChange}
                        label='Confirm APassword'
                        required
                        />
                        <CustomButton type='submit'>
                            Sign UP!!!
                        </CustomButton>

                </form>
            </div>
        )
    }
}


export default SignUp;
